preConditions.sqlCheck:
select COUNT(*) from concept_set cs
join concept_name cn
on cs.concept_id = cn.concept_id
where cs.concept_id IN (select concept_id from concept_name where name IN('Numération reticulocyte','Typage anémie','Sang Frais (Goutte fraiche)','GE','Selles Directs - KOP','Numeration GB','Sang (hématurie)','Test de Grossesse','Rivalta (Pus)','Rivalta (Crachat)','Rivalta (Gastrique)','Rivalta (Ascite)','Rivalta (Pleural)','Rivalta (Ganglionnaire)','Rivalta (Synovial)','Selles Directs - Type','Selles I.O','FL - LYM%','FL - MXD%','FL - NEUT%','Crag','Encre de chine','Proteinorachie (test de Pandy)','Gram','(If positif) Forme','(If positif) Coloration','(If positif) Groupement','Microscopie TB - Recherche de BAAR (LCR)','Urobilinogène','Bilirubine','Protéines','Nitrites','Corps cétoniques','Acide ascorbique','Glucose','pH','Leucocytes'
) AND concept_name_type = "FULLY_SPECIFIED")
and cs.concept_set IN (select concept_id from concept_name where name = '1');

