preConditions.sqlCheck:
select COUNT(*) from concept_set cs
join concept_name cn
on cs.concept_id = cn.concept_id
where cs.concept_id IN (select concept_id from concept_name where name IN('GeneXpert TB (LCR)','Genexpert (Crachat)','Genexpert (Pus)','Genexpert (Gastrique)','Genexpert (Ascite)','Genexpert (Pleural)','Genexpert (Ganglionnaire)','Genexpert (Synovial)','Microscopie TB - Recherche de BAAR (Crachat)','Microscopie TB - Recherche de BAAR (Pus)','Microscopie TB - Recherche de BAAR (Gastrique)','Microscopie TB - Recherche de BAAR (Ascite)','Microscopie TB - Recherche de BAAR (Pleural)','Microscopie TB - Recherche de BAAR (Ganglionnaire)','Microscopie TB - Recherche de BAAR (Synovial)'
) AND concept_name_type = "FULLY_SPECIFIED")
and cs.concept_set IN (select concept_id from concept_name where name = 'TB');

