preConditions.sqlCheck:
select COUNT(*) from concept_set cs
join concept_name cn
on cs.concept_id = cn.concept_id
where cs.concept_id IN (select concept_id from concept_name where name IN('Sang','Selles (Sample)','Urine','LCR (Sample)','Crachat',"Liquide d'épanchement pleural",'Liquide ascite','Pus','Suc ganglionnaire','Liquide Synovial','Suc gastrique'
) AND concept_name_type = "FULLY_SPECIFIED")
and cs.concept_set IN (select concept_id from concept_name where name = 'Lab Samples');

